import express from 'express' //ESModules
//const express = require('express') => commonjs

import diaryRouter from '../diaryRouter'

const app = express()
app.use(express.json())  //middlenare que transforma la reg.body a un json

const PORT = 3000

app.get('/ping', (_reg, _res) => {
    console.log('someone pinged here!! + new Date().tocaleDateString()')
   _res.send('pong')
})

app.use('/api/diaries', diaryRouter)

app.listen(PORT, () => {
    console.log('Server running on port $(PORT)')
})
